let renderDay;
$(document).ready(() => {
  renderDay = function(array) {
    $('.event').each(function(i, el) {
      $(el).remove();
    });
    // background colors for events
    let arrayOfColors = [
      'rgba(255, 153, 153, 0.75)',
      'rgba(255, 204, 153, 0.75)',
